mapper.update:
UPDATE mvcboard
SET visitcount = visitcount + 1
WHERE idx = ${idx}

UPDATE mvcboard
SET downcount = downcount + 1
WHERE idx = ${idx}

mapper.select:
SELECT COUNT(*)
FROM mvcboard
WHERE pass = #{pass}
AND idx = #{idx}

mapper.update:
UPDATE mvcboard
SET title = #{title}, name = #{name}, content = #{content}, ofile = #{ofile}, sfile = #{sfile}
WHERE idx = #{idx} AND pass = #{pass}
